frappe.provide('erpnext');

$(document).bind('toolbar_setup', function () {
	frappe.app.name = "AxiraERP";

	frappe.help_feedback_link = '<p><a class="text-muted" \
		href="http://axiraerp.com">Feedback</a></p>';

	$('.navbar-home').html('<img class="erpnext-icon" src="' + frappe.urllib.get_base_url() + '/assets/erpnext/images/erp-icon.svg" />');

	$('[data-link="docs"]').attr("href", "http://www.ibtevolve.com/corporate/corporate-overview/#");
	$('[data-link="issues"]').attr("href", "http://www.ibtevolve.com/corporate/corporate-overview/#");

	var $help_menu = $('.dropdown-help ul .documentation-links');

	$('<li><a data-link-type="forum" href="http://axiraerp.com" \
		target="_blank">' + __('User Forum') + '</a></li>').insertBefore($help_menu);
	$('<li><a href="https://ibtevolve.slack.com" \
		target="_blank">' + __('Chat') + '</a></li>').insertBefore($help_menu);
	$('<li><a href="http://www.ibtevolve.com/contact/" \
		target="_blank">' + __('Report an Issue') + '</a></li>').insertBefore($help_menu);
	$( "div" ).remove(".module-section-column:contains('Help')");
});

$(document).load(function(){
	$("div").remove(".module-section-column:contains('Help')");
	$("div .h4").addClass('testload');
	alert('Load event');
});

$('a.module-link').click(function(){
	$("div").remove(".module-section-column:contains('Help')");
	alert('Click sevent');
});

$(document).ready(function() { 
	
	$("div .h4").addClass('testready');
});

$(window).bind("load", function() { 
	$.ajax({
		// Disable caching of AJAX responses
		cache: false
	});
	$("div").remove(".module-section-column:contains('Help')");
	$("div .h4").addClass('test');
	// alert("Window Load min ready");
});

$("modules").load(".module-section-column", function() {
	$("div").remove(".module-section-column:contains('Help')");
});

